
public class StringSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String a = "2";
		
		
		switch(a)
		{
		
		case "1":
			System.out.println("Hello I am 1");
			break;
		case "2":
			System.out.println("Hello I am 2");
			break;
		case "3":
		    System.out.println("Hello I am 3");
		    break;
		default:
			System.out.println("Sorry ! Not Present");
		
		
		
		}
		
		
		
		
		
	}

}
